DROP TABLE IF EXISTS `order_handover_flow`;
CREATE TABLE `order_handover_flow` (
  `id` bigint(20) NOT NULL COMMENT 'ID',
  `code` varchar(64) NOT NULL COMMENT '编号',
  `handover_reason` varchar(2) DEFAULT NULL COMMENT '交接原因（-1日常交接 0：晋升，1：转岗，2：离职 3：平调）',
  `handover_user` bigint(20) DEFAULT NULL COMMENT '交接人ID',
  `handover_user_dept` bigint(20) DEFAULT NULL COMMENT '交接人部门',
  `receive_dept` bigint(20) DEFAULT NULL COMMENT '接收部门',
  `receive_user` bigint(20) DEFAULT NULL COMMENT '接收人ID',
  `type` varchar(2) DEFAULT NULL COMMENT '交接类型：-1 任务交接 0 公共交接',
  `flow_key` varchar(255) DEFAULT NULL COMMENT '流程名称',
  `flow_inst_id` bigint(64) DEFAULT NULL COMMENT '流程ID',
  `create_user` bigint(20) DEFAULT NULL COMMENT '创建人',
  `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_user` bigint(20) DEFAULT NULL COMMENT '修改人',
  `update_time` datetime DEFAULT NULL COMMENT '修改时间',
  `status` varchar(2) NOT NULL DEFAULT '-2' COMMENT '状态 -3撤回 -2: 未交接 -1 交接中 0：已交接 1：作废 2终止',
  `del_flag` char(1) DEFAULT '0' COMMENT '删除标识',
  `finish_time` datetime DEFAULT NULL COMMENT '完成时间',
  `tenant_id` bigint(20) NOT NULL COMMENT '租户ID',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 ROW_FORMAT=DYNAMIC COMMENT='交接流程：三种岗位（公共）人员离职、晋升、调岗/平调的工作/岗位变更';

